import React, {useState} from 'react'


export default function TextForm(props) {
    const handleUpClick = ()=>{
        // console.log("Uppercase was clicket: " + text);
        let newText = text.toUpperCase();
        setText(newText)
    }

    const handleLoClick = ()=>{
      // console.log("Uppercase was clicket: " + text);
      let newText = text.toLowerCase();
      setText(newText)
    }
    const handleClearClick = ()=>{
      // console.log("Uppercase was clicket: " + text);
      let newText = '';
      setText(newText)
    }

    const handleOnChange = (event)=>{
        // console.log("On change");
        setText(event.target.value);
    }

    const [text, setText] = useState('');
    // text = "new text"; Wrong  way to change state
    // setText = "new text"; Wrong  way to change state
  return (
    <>
<div className='contaoneer'>
    <h1>{props.heading}</h1>
<div className="mb-3">
<textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>
</div>
<button className="btn btn-primary mx-1" onClick={handleUpClick} >Uppercase</button>
<button className="btn btn-primary mx-1" onClick={handleLoClick} >Lowercase</button>
<button className="btn btn-primary mx-1" onClick={handleClearClick} >Clear</button>
</div>
<div className="container my-3">
<h2>Your Text Summary</h2>
<p>{text.split(" ").length} words and {text.length} characters</p>
<p>{0.008 * text.split(" ").length} characters</p>
<h2>Preview</h2>
<p>{text}</p>
</div>
</>
  )
}
